package com.lion.testutil;import java.util.function.Function;/** * 基础的方法类 * 需要实现run()函数: 执行的主函数 *        prepare()函数: 执行主函数前的准备函数 * BasicFunction类中，当ready为true时，将不会执行prepare()函数 * */public abstract class BasicFunction<T, R> implements Function<T, R> {    private boolean ready = false;    public abstract void prepare();    public abstract R run(T params);    public void setReady(boolean ready){        this.ready = ready;    }    public boolean getReady(){        return this.ready;    }    public R apply(T params){        if(!ready){            prepare();        }        return run(params);    }}