package com.yeadun.testutil.excel;import java.util.ArrayList;import java.util.Arrays;public enum ExcelHeaderType {    CERT_NAME("certName",            "姓名", "名字"),    MP("mp",            "手机号", "手机", "电话", "电话号", "电号号码"),    CERT_ID("certId",            "身份证号", "身份证"),    CARD_ID("cardId",            "银行卡号", "银行号", "卡号");    private String dist;    private ArrayList<String> res = new ArrayList<>();    ExcelHeaderType(String dist, String... res) {        this.dist = dist;        if (res != null) {            this.res.addAll(Arrays.asList(res));        }    }    public String getDist() {        return this.dist;    }    public ArrayList<String> getRes(){        return this.res;    }    public String getAllRes() {        StringBuilder builder = new StringBuilder();        for (String r : this.res) {            builder.append("[").append(r).append("] ");        }        return builder.toString();    }    }