package com.yeadun.testutil.net;public enum HttpHeader {    URLENCODED("Content-Type", "application/x-www-form-urlencoded"),    CHARSET_UTF8("charset", "UTF-8"),    CHARSET_GBK("charset", "GBK"),    ACCEPT_CHARSET_UTF8("Accept-Charset", "UTF-8");    private String key;    private String value;    HttpHeader(String key, String value){        this.key = key;        this.value = value;    }    public String getKey() {        return key;    }    public String getValue() {        return value;    }    public String toString(){        return "[ " + this.key +": " + this.value + " ]";    }}