package com.yeadun.testutil.product.model;import com.yeadun.testutil.product.ProductType;import java.util.HashMap;public class NullPOJO implements Product{    private ProductType type;    public NullPOJO(ProductType type){        this.type = type;    }    public String getType() {        return type.getProdCode() + "(" + type.getProdName() + ") 无返回结果。";    }    public String toString(){        return getType();    }    @Override    public HashMap<String, String> pojo2Map() {        HashMap<String, String> result = new HashMap<>();        result.put("info", getType());        return result;    }}