package com.yeadun.testutil.util.format;import com.alibaba.fastjson.JSON;import com.yeadun.testutil.product.Response;import org.slf4j.Logger;import org.slf4j.LoggerFactory;public class ProductResponseFormat {    private static final Logger LOG = LoggerFactory.getLogger(ProductResponseFormat.class);    /**     * 将JSON字符串转为ApiBasicResult对象并返回     *     * @param res JSON字符串     * @return ApiBasicResult对象     */    public static Response formatReturnJsonStr(String res) {        return JSON.parseObject(res, Response.class);    }}